public class TestDataParser {

	@Keyword
	def List<Map> toMapList(TestData tda) {
		def cols = tda.getColumnNames()
		def data = tda.getAllData()
		def result = []
		data.each {
			def map = [:]
			for (int i = 0; i < cols.length; i++) {
				map.put(cols[i], it[i])
			}
			result.add(map)
		}
		result
	}
}
